#
# goal: create a dictionary indexed by id.
# each item of the dictionary will be a tuple.
# the first 114 will denote the 1-hot vector for the hero. (there is a missing hero??)
# the next bit will represent the attack type. 1 for melee 0 for range
# the next 3 bits will be a 1 hot to represent the main attr. str, agi and int is the order.
# the next 9 bits will describe the roles:
# the order is Support, Jungler, Escape, Carry, Durable, Nuker, Pusher, Disabler, Initiator.

HeroDictionary = {}
Roles = ['Support', 'Jungler', 'Escape', 'Carry', 'Durable', 'Nuker', 'Pusher', 'Disabler', 'Initiator']

for i in heroesById.keys():
    HeroDictionary[i] =[]
    onehot = [1 if x+1 == i else 0 for x in range(0,len(heroes)+1)]
    HeroDictionary[i].append(onehot)
    if heroesById[i]['attack_type'] == 'Melee':
        HeroDictionary[i].append([1])
    else:
        HeroDictionary[i].append([0])
    if heroesById[i]['primary_attr'] == 'str':
        HeroDictionary[i].append([1,0,0])
    elif heroesById[i]['primary_attr'] == 'agi':
        HeroDictionary[i].append([0,1,0])
    else:
        HeroDictionary[i].append([0,0,1])
    roles =[1 if Roles[t] in heroesById[i]['roles'] else 0 for t in range(9)]
    HeroDictionary[i].append(roles)

flatten = lambda l: [item for sublist in l for item in sublist]

for t in HeroDictionary.keys():
    HeroDictionary[t] = flatten(HeroDictionary[t])


pickle.dump(HeroDictionary, open('HeroDict', 'wb'))